    /**
     * Test of normalize method, of class LogGap.
     */
    @Test
    public void testNormalize() {
        System.out.println("normalize");
        double[] values = new double[] { 1,2,3,3,4,5,2,4,5 };
        double d = 0.0;
        LogGap instance = new LogGap();
        instance.makeModel(values);
        for(int i = 0; i < 7; i++) {
            assert(Double.isFinite(instance.normalize((double)i)));
        }
         
        assert(Double.isFinite(instance.normalize(2.1)));
    }
    
